import Footer from '../components/Footer';
import Navbar from '../components/Navbar';
import { redirect } from 'react-router-dom';
import { useState } from 'react';

export default function Contact() {
  const [form, setForm] = useState(true)
  const [finish, setFinish] = useState(false)

  const handleClick = () => {
    setForm(false)
    setFinish(true)
  }

  return (
    <>
      <Navbar />
      <div class="contact-container">
        {form && (
          <div>
          <h3>Fala com a gente</h3>
          <p>Preencha o formul√°rio ao lado ou entre em contato por meio de nossas redes sociais:</p>
          </div>
        )}
        {finish && (
          <>
            <div>
              <h3>Obrigado</h3>
              <p>Em breve, entraremos em contato</p>
            </div>
            <div></div>
          </>
        )}
        {form && (
          <div>
            <form
              action="https://formsubmit.co/ygormaiaa1@gmail.com"
              method="POST"
              class="form"
            >
              <label for="name">Nome *</label>
              <input type="text" name="name" id="name" required />
              <label for="email">E-mail *</label>
              <input type="email" name="email" id="email" required />
              <label for="message">Mensagem *</label>
              <textarea name="message" id="message" required></textarea>
              <input type="hidden" name="_captcha" value="false" />
              <div class="cta-button"> 
                <button type="submit"><a href="/finish">enviar</a></button>
              </div>
            </form>
          </div>
        )}
      </div>
      <Footer />
    </>
  )
}
